students = User.all
teaching_language_session = TeachingLanguageSession.all
students.each do |student|
  3.times do
    teaching_language_session_id = teaching_language_session.sample.id
    Booking.create!(
      date: Date.today,
      time_in: Time.now,
      user_id: student.id,
      teaching_language_session_id: teaching_language_session_id
    )
    Booking.create!(
      date: Date.yesterday,
      time_in: Time.now,
      user_id: student.id,
      teaching_language_session_id: teaching_language_session_id
