students_data.each do |student_info|
  user_student = User.create!(
    email: student_info[:email],
    role: 0,
    password: "123456",
    first_name: student_info[:first_name],
    last_name: student_info[:last_name],
    country: student_info[:country],
    city: student_info[:city],
    date_of_birth: student_info[:date_of_birth],
    about_me: student_info[:about_me],
    native_language: student_info[:native_language],
    other_languages: student_info[:other_languages],
    learning_languages: student_info[:learning_languages],
    objectives: "poliglota"
  )

  profile_photo = URI.open(student_info[:profile_photo])
  user_student.profile_photo.attach(io: profile_photo, filename: "#{student_info[:first_name].downcase}_profile_image.png", content_type: "image/png")
  user_student.save!

  banner_image = URI.open(student_info[:banner_image])
  user_student.banner_image.attach(io: banner_image, filename: "#{student_info[:first_name].downcase}_banner_image.png", content_type: "image/png")
  user_student.save!

  student_info[:photo_album].split(',').each do |photo_url|
    photo = URI.open(photo_url.strip)
    user_student.photo_album.attach(io: photo, filename: "#{student_info[:first_name].downcase}_photo_album_#{Time.now.to_i}.png", content_type: "image/png")
  end
  user_student.save!

  puts "creando bookings"

  teaching_language_sessions = user_student.teaching_language_sessions

  teaching_language_sessions.each do |teaching_language_session|
    Booking.create!(
      user: user_student,
      teaching_language_session: teaching_language_session,
      date: "2023-12-17".to_date, 
      time_in: "19:00:00 -0300".to_time  
    )
  end
end

